const obj = { 'a': 1, 'b': 'bb', 'c': { 'd': 'dd' } };
const map = new Map ( Object.entries ( obj ) );

function arrMapEntries ( map_ini ) {
    let resArr = [];
    for ( let [ key, value ] of map_ini.entries () ) {
        resArr.push ( [ key, value ] );
    }
    return resArr;
}

const arr_map = arrMapEntries ( map );
